import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getUsers } from '../actions/userData';

class UserList extends Component {

  componentDidMount() {
    this.getUsers();
  }
  getUsers() {
    dispatch({ type: FETCH_USERS_START });
    // then... start the API call
    // axiosWithAuth()
    axios
      .get(`https://usemytechstuff.herokuapp.com/api/users`)
      .then(res => {
        console.log("RESPONSE", res);
        dispatch({
          type: FETCH_USERS_SUCCESS,
          payload: res.data
        });
      })
      .catch(err => {
        console.log("ERRRRRRRRRRRRRRRRRR", err);
        dispatch({
          type: FETCH_USERS_FAILURE,
          payload: err
        })
      })
  }

  state = {
    users: [],
  }
  render() {
    return (
      <div className='user-wrapper'>
        <h2>Techies</h2>
        {this.props.users.map((users, id) => (
          <div className='user-card'>
            <h3>Username: {users.username}</h3>
            <p>Password: {users.password}</p>
            <p>Email: {users.email}</p>
          </div>
        ))}
      </div>
    )
  }
};


const mapStateToProps = ({ users, fetchingUsers }) => ({
  users,
  fetchingUsers,
});

export default connect(
  mapStateToProps,
  { getUsers }
)(UserList);
