import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getUsers, } from '../actions/userData';
import axios from 'axios';
import styled from 'styled-components';

const UserWrapper = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  align-items: center;
  justify-content: center;
  text-align: left;
  margin: 2% auto;
`
const Title = styled.div`
  font-size: 2rem;
`
class UserList extends Component {

  componentDidMount() {
    this.getUsers();
  }
  getUsers() {

    // then... start the API call
    // axiosWithAuth()
    axios
      .get(`https://usemytechstuff.herokuapp.com/api/users`)
      .then(res => {
        console.log("RESPONSE", res);
        this.setState({
          users: res.data
        })
      })
      .catch(err => {
        console.log("ERRRRRRRRRRRRRRRRRR", err);
      })
  }

  // componentDidMount() {
  //   this.deleteUser();
  // }
  // deleteUser() {
  //   axios
  //     .delete(`${'https://usemytechstuff.herokuapp.com/api/users'}/${id}`)
  //     .then(res => {
  //       this.setState({

  //       })
  //     })
  //     .catch(err => {
  //       console.log("=======ERRRRRR=======", err);
  //     })
  // }

  state = {
    users: [],
  }
  render() {
    return (
      <UserWrapper>
        <Title>Techies</Title>
        {this.state.users.map((users, id) => (
          <div className='user-card'>
            <h3>Username: {users.username}</h3>
            <p>Email: {users.email}</p>
            <img src={users.picture} alt="alt-img" />
            <br /><button className='delete-btn' onClick={e => {
              // this.props.deleteUser(this.props.id);
            }}>Delete Account</button>
          </div>
        ))}
      </UserWrapper>
    )
  }
};


const mapStateToProps = ({ fetchingUsers, deletingUser }) => ({
  fetchingUsers, deletingUser
});

export default connect(
  mapStateToProps,
  { getUsers, }
)(UserList);
