import React, { Component } from 'react'
import { connect } from 'react-redux';
import { addUser, getUsers } from '../actions/signUp';
import { UserList } from './UserList';

class SignUpForm extends Component {
  state = {
    newUser: {
      username: "",
      password: "",
      email: "",
    }
  }

  componentDidMount() {
    this.props.getUsers();
  }


  handleChange = e => {
    console.log(e);
    this.setState({
      newUser: {
        ...this.state.newUser,
        [e.target.name]: e.target.value
      }
    })
  }

  handleSubmit = e => {
    e.preventDefault();
    this.props.addUser(this.state.newUser)
      .then(() =>
        this.props.history.push("/users"))
    console.log(this.state);

    this.setState({
      newUser: {
        username: "",
        password: "",
        email: "",
      }
    })
  }


  render() {
    return (
      <div className="container">
        <div>
          {this.props.users.map(user => {
            return (
              <UserList
                username={user.username}
                password={user.password}
                email={user.email}
                key={user.id}
              />
            )
          })}
        </div>
        <form onSubmit={this.handleSubmit} className="white">
          <h5 className="grey-text text-darken-3">Sign Up</h5>
          <div className="input-field">
            <label
              htmlFor="username">Create a Username</label>
            <input
              type="text"
              name="username"
              value={this.state.newUser.userName}
              onChange={this.handleChange}
            />
          </div>
          <div className="input-field">
            <label
              htmlFor="password">Create a Password</label>
            <input
              type="text"
              name="password"
              value={this.state.newUser.password}
              onChange={this.handleChange}
            />
          </div>
          <div className="input-field">
            <label
              htmlFor="email">Email</label>
            <input
              type="email"
              name="email"
              value={this.state.newUser.email}
              onChange={this.handleChange}
            />
          </div>
          <div className="input-field">
            <button className="waves-effect waves-light btn #1e88e5 blue darken-1 white-text  z-depth-1">Sign Up</button>
          </div>
        </form>
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    fetchingUsers: state.fetchingUsers,
    addingUser: state.addingUser,
  }
}

// const mapDispatchToProps = (dispatch) => {
//   return {
//     addUser: (newUser) => dispatch(addUser(newUser))
//   }
// }

export default connect(
  mapStateToProps,
  { getUsers, addUser }
)(SignUpForm);
