import React, { Component } from 'react'
import { connect } from 'react-redux';
import { addUser, getUsers } from '../actions/SignUp';
import styled from 'styled-components';
// import UserList from './UserList';


const Wrapper = styled.div`
 // height: 100vh;
  margin-top: 50px; 
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;
const FormWrapper = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  padding: 20px 40px;
  border: 1px solid lightgrey;
  ${'' /* border-radius: 10px; */}
  ${'' /* box-shadow: 0px 10px 50px #555; */}
  background-color: #ffffff;
    background: gainsboro;
`;
const Form = styled.form`
  width: 100%;
  display: flex;
  flex-wrap: wrap;
  border: none;
`;
const HTwo = styled.div`
  font-size: 1.7rem;
  text-align: center;
  width: 100%;
  color: #111;
  font-weight: lighter;
  ${'' /* font-family: Oxygen, sans-serif; */}
`;
const Label = styled.div`
  width:90%;
  font-size: 1.1rem;
  margin-bottom: 0.25em;
  color: #222;
  font-weight: lighter;
  ${'' /* font-family: Oxygen, sans-serif; */}
`;
const Input = styled.input`
  padding: 2%;
  font-size: .85rem;
  border-radius: 5px;
  width: 100%;
  outline: none;
  border: 1px solid #cfcfcf;
`;
const CreatAccount = styled.div`
  width: 95%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Button = styled.button`
  color: white;
  background: green;
  border: 2px solid dodgerblue;
  border-radius: 10px;
  width: 100%;
  margin-top: 1em;
  padding: 5px 0px;
  font-size: 1em;
  font-weight: lighter;
  letter-spacing: 1px;
  margin-bottom: 0.25em;
  cursor: pointer;
  &:hover {
  color: white;
  font-weight: bold;
  background-color: seagreen;
  border: 2px solid #519e8a;
  }
`;


const CancelButton = styled(Button)`
  background: lightpink;
  width: 80%;
  margin: 0 auto;
  &:hover {
  font-weight: bold;
  background-color: lightcoral;
  }
`;

class SignUpForm extends Component {
  state = {
    users: [],
    newUser: {
      username: "",
      password: "",
      email: "",
    }
  };
  componentDidMount() {
    this.props.getUsers();
  }


  handleChange = e => {
    console.log(e);
    this.setState({
      newUser: {
        ...this.state.newUser,
        [e.target.name]: e.target.value
      }
    })
  };

  handleSubmit = e => {
    e.preventDefault();
    this.props.addUser(this.state.newUser);
    console.log(this.state);
    this.setState({
      newUser: {
        username: "",
        password: "",
        email: "",
      }
    });
    this.addForm.reset();
  };


  render() {
    return (

      <Wrapper>
        <FormWrapper>
          <HTwo>Create Account</HTwo>
          <Form ref={input => this.addForm = input} onSubmit={this.handleSubmit}>
            <Label> Username:
              <Input
                placeholder="Create a Username"
                type="text"
                name="username"
                value={this.state.newUser.userName}
                onChange={this.handleChange}
              />
            </Label>
            <Label> Password
              <Input
                placeholder="Create a Password"
                type="password"
                name="password"
                value={this.state.newUser.password}
                onChange={this.handleChange}
              />
            </Label>
            <Label> email
              <Input
                placeholder="Email"
                type="email"
                name="email"
                value={this.state.newUser.email}
                onChange={this.handleChange}
              />
            </Label>
            <CreatAccount>
              <Button type="submit">Sign Up</Button>
            </CreatAccount>

            <CancelButton
              className = "cancelButton"
              onClick = { () => this.props.history.push("/") }
            >  Cancel !!! </CancelButton>

          </Form>
        </FormWrapper>





      </Wrapper>
    )
  }
}

const mapStateToProps = state => {
  return {
    users: state.users,
    fetchingUsers: state.fetchingUsers,
    addingUser: state.addingUser,
  }
}


export default connect(
  mapStateToProps,
  { getUsers, addUser }
)(SignUpForm);
