import React, { Component } from 'react'
import { connect } from 'react-redux';
import { addUser, getUsers } from '../actions/SignUp';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
// import UserList from './UserList';

const Wrapper = styled.div`
  margin-top: 5%;
  width: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  ${'' /* background-color: lightgre; */}
`

const FormWrapper = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  padding: 20px 40px;
  border-radius: 10px;
  box-shadow: 0px 10px 50px #555;
  background-color: #ffffff;
`

class SignUpForm extends Component {
  state = {
    users: [],
    newUser: {
      username: "",
      password: "",
      email: "",
    }
  }
  componentDidMount() {
    this.props.getUsers();
  }


  handleChange = e => {
    console.log(e);
    this.setState({
      newUser: {
        ...this.state.newUser,
        [e.target.name]: e.target.value
      }
    })
  }

  handleSubmit = e => {
    e.preventDefault();
    this.props.addUser(this.state.newUser)
    // .then(() =>
    //   this.props.history.push("/users"))
    console.log(this.state);

    this.setState({
      newUser: {
        username: "",
        password: "",
        email: "",
      }
    })
  }


  render() {
    return (
      <Wrapper>
        {/* <div>
          {this.props.users.map(user => {
            return (
              <UserList
                username={user.username}
                password={user.password}
                email={user.email}
                key={user.id}
              />
            )
          })}
        </div> */}
        <FormWrapper>
          <h2>Create Account</h2>
          <form onSubmit={this.handleSubmit}>
            <div className="user-name">
              <label
                htmlFor="username">Create a Username</label>
              <input
                type="text"
                name="username"
                value={this.state.newUser.userName}
                onChange={this.handleChange}
              />
            </div>
            <div className="password">
              <label
                htmlFor="password">Create a Password</label>
              <input
                type="text"
                name="password"
                value={this.state.newUser.password}
                onChange={this.handleChange}
              />
            </div>
            <div className="email">
              <label
                htmlFor="email">Email</label>
              <input
                type="email"
                name="email"
                value={this.state.newUser.email}
                onChange={this.handleChange}
              />
            </div>
            <div className="createAccount">
              <button type="submit">Create Account</button>
              <small><Link to="/login">Already Have an Account?</Link></small>
            </div>
          </form>
        </FormWrapper>
      </Wrapper>
    )
  }
}

const mapStateToProps = state => {
  return {
    users: state.users,
    fetchingUsers: state.fetchingUsers,
    addingUser: state.addingUser,
  }
}

// const mapDispatchToProps = (dispatch) => {
//   return {
//     addUser: (newUser) => dispatch(addUser(newUser))
//   }
// }

export default connect(
  mapStateToProps,
  { getUsers, addUser }
)(SignUpForm);
